INSERT INTO `wp_postmeta` VALUES (18689,13142,'Sector','GALACTIC CROWDSOURCING');
INSERT INTO `wp_postmeta` VALUES (18690,13142,'Description Content','<p>NASA has tough challenges. With scores of wildly different and complex projects in their evolving pipeline, NASA has a difficult time consistently sourcing innovative solutions. Traditional paths to address pressing challenges meet a limited pool of potential contributors. NASA needed a new way to draw new innovators to their challenges in a cost effective, highly scalable manner.\r\n</p>');
INSERT INTO `wp_postmeta` VALUES (18691,13142,'Sidebar Content','<p>PROJECTS</p>\r\n<br />\r\n<p><a href=\"http://www.topcoder.com/dtn\">NASA&#8217;s Disruption Tolerant Networking Challenge Series</a></p>\r\n<br />\r\n<p><a href=\"http://www.topcoder.com/iss\">NASA&#8217;s International Space Station Challenge Series</a></p>\r\n<br />\r\n<p><a href=\"http://www.topcoder.com/collectiveminds/\">NatGeoCollective Minds &#038; Machines Exploration Challenge</a></p>\r\n<br />\r\n<p><a href=\"http://community.topcoder.com/coeci/\">Center of Excellence for Collaborative Innovation</a></p>\r\n<br />\r\n<p><a href=\"http://www.topcoder.com/epa/\">United States Environmental Protection Agency Series</a></p>\r\n<br />\r\n\r\n<p><iframe src=\"//www.youtube.com/embed/re33w8u8asQ\" frameborder=\"0\" allowfullscreen></iframe></p>\r\n<br />');
INSERT INTO `wp_postmeta` VALUES (18692,13142,'Quote','The NASA Tournament Lab is enabling NASA to accelerate technical capabilities while at the same time advancing our knowledge of using open innovation for impactful results on our missions.');
INSERT INTO `wp_postmeta` VALUES (18693,13142,'Quote author','Jason Crusan');
INSERT INTO `wp_postmeta` VALUES (18694,13142,'Author designation','Director, Advanced Exploration Systems');
