INSERT INTO `wp_postmeta` VALUES (18697,13145,'Sector','MEGABLAST ANTIBODY SEQUENCING ALGORITHM');
INSERT INTO `wp_postmeta` VALUES (18698,13145,'Description Content','<p>Cutting-edge computational work is consistently under-resourced. To counteract this challenge, it traditionally requires deep pockets or access to talented coders and algorithmists who are willing to spend an exorbitant amount of time on the project.</p>\r\n<p>Harvard Medical School was facing this exact challenge for a complex DNA sequencing alignment challenge. This was a real-world problem, where the limitations of existing tools severely constrained the ability to pioneer new advances in medical knowledge.\r\n</p>');
INSERT INTO `wp_postmeta` VALUES (18699,13145,'Sidebar Content','<iframe width=\"100%\" src=\"//www.youtube.com/embed/sVe_pjtKAWE\" frameborder=\"0\" allowfullscreen></iframe>');
INSERT INTO `wp_postmeta` VALUES (18700,13145,'Quote','TopCoder surpassed expectations: a two-week competition led to code that was just as good but almost three orders of magnitude faster for a few thousand dollars. Hard to imagine beating that.');
