INSERT INTO `wp_posts` VALUES (197,3,'2013-12-06 03:50:03','2013-12-06 03:50:03','<h1>WHITEPAPERS, EBOOKS, VIDEO, AND MORE</h1>\r\n<br />\r\n<p>Research and educational material on how enterprise IT is using crowdsourcing to drive innovation.</p>\r\n\r\n<section class=\"resourceList\">\r\n<ul>\r\n	<li><a class=\"contentLink contentTilte\" href=\"#\"><img class=\"resourceThumb\" alt=\"document\" src=\"/wp-content/uploads/2013/10/document.png\" width=\"227\" height=\"130\" />Whitepaper: Crowdsourcing Development for the Boundary-free Enterprise</a>\r\n<span class=\"contentBrief\">Explore independent research from Saugatuck Research on Enterprise Crowdsourcing with CloudSpokes.</span></li>\r\n	<li><a class=\"contentLink contentTilte\" href=\"#\"><img class=\"resourceThumb\" alt=\"Screen shot 2013-04-04 at 3.55.14 PM\" src=\"/wp-content/uploads/2013/10/webinar_saugatuck.png\" width=\"225\" height=\"127\" />Webinar: How Community Development Can Improve Project Success And Increase Innovation</a>\r\n<span class=\"contentBrief\">Mike West of Saugatuck, and Mike Morris, GM of CloudSpokes, discuss the benefits of a community software development model.</span></li>\r\n	<li><a class=\"contentLink contentTilte\" href=\"#\"><img class=\"resourceThumb\" alt=\"webinar_saugatuck\" src=\"/wp-content/uploads/2013/10/cloudspokes_utest_webinar.png\" width=\"227\" height=\"130\" />Webinar: uTest &amp; CloudSpokes, the Crowdsourced Development Lifecycle</a>\r\n<span class=\"contentBrief\">This first of it\'s kind webinar with uTest to show the new power of community development and testing.</span></li>\r\n</ul>\r\n</section>','Resources','','publish','open','open','','resources','','','2013-12-09 15:17:49','2013-12-09 20:17:49','',0,'http://192.168.137.1/1.Create/TCS/lt/wp/?page_id=197',0,'page','',0);
